mapper.select:
SELECT c.id AS id, c.coupon_id AS couponId, c.user_id AS userId, c.code AS code, c.status AS status, c.description AS description, c.gmt_start as gmtStart, c.gmt_end as gmtEnd FROM unimall_coupon_code c WHERE 1 = 1
AND c.coupon_id = #{couponId}
AND c.user_id = #{userId}
AND c.status = #{status}
order BY c.id DESC limit #{offset},#{limit}
