mapper.select:
SELECT id, name, degree, percent, money, img, description, gmt_update AS gmtUpdate, gmt_create AS gmtCreate FROM unimall_member_level WHERE 1 = 1
AND id = #{id}
AND name like concat(concat("%",#{name}),"%")
AND `degree` = #{degree}
AND percent = #{percent}
AND description like concat(concat("%",#{description}),"%")
limit #{offset},#{limit}

SELECT count(1) FROM unimall_member_level WHERE 1 = 1
AND id = #{id}
AND name like concat(concat("%",#{name}),"%")
AND `degree` = #{degree}
AND percent = #{percent}
AND description like concat(concat("%",#{description}),"%")
